// Simple function for the pages to know whether a user has signed in or not
import React, { createContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isSignedIn, setIsSignedIn] = useState(false);

    useEffect(() => {
        // Check token from secure storage 
        const token = document.cookie.includes('authToken'); // Simplified example
        setIsSignedIn(!!token);
    }, []);

    const login = (token) => {
        document.cookie = `authToken=${token}; Secure; HttpOnly; Path=/`;
        setIsSignedIn(true);
    };

    const logout = () => {
        document.cookie = 'authToken=; Max-Age=0; Path=/';
        setIsSignedIn(false);
    };

    return (
        <AuthContext.Provider value={{ isSignedIn, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => React.useContext(AuthContext);
