// Simple function for the pages to know whether a user has signed in or not
import React, { createContext, useState } from 'react';
const SignInContext = createContext();

export const SignedInProvider = ({ children }) => {
    const [isSignedIn, setSignedIn] = useState(false);

    const login = () => setSignedIn(true);
    const logout = () => setSignedIn(false);
    const getSignInStatus = () => isSignedIn;

    return (
        <SignInContext.Provider value={{ login, logout, getSignInStatus }}>
            {children}
        </SignInContext.Provider>
    );
}
