// Simple function for the pages to know whether a user has signed in or not
import react, { useState } from 'react';

const [SignIn, setSignIn] = useState(false);

export const setSignedIn = () => {
    if (SignIn === true) {
        setSignIn(false);
    }
    else if (SignIn === false) {
        setSignIn(true);
    }
}

export const isSignedIn = () => {

    return SignIn;
}
